// components/AddTask.js
import { useState } from 'react';
import { VISIBILITY_LEVELS } from '../types';
import { Card, CardHeader, CardContent } from './ui/card';
import { Button } from './ui/button';
import { Input } from './ui/input';
import { Select } from './ui/select';

/**
 * Form component for adding new tasks.
 * Handles task creation with validation and proper data formatting.
 * 
 * @param {Object} props - Component props
 * @param {Function} props.onAdd - Callback function to handle task addition
 */
export function AddTask({ onAdd }) {
  // Initialize form state with default values
  const [formData, setFormData] = useState({
    title: '',
    priority: 'Medium',
    dueDate: '',
    reward: ''
  });

  /**
   * Universal change handler for all form inputs.
   * Updates form state based on input changes.
   * 
   * @param {Event} e - Change event from form input
   */
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  /**
   * Handles form submission.
   * Validates input and creates new task with proper formatting.
   * 
   * @param {Event} e - Form submission event
   */
  const handleSubmit = (e) => {
    e.preventDefault();

    // Basic validation
    if (!formData.title.trim()) {
      alert('Please enter a task title');
      return;
    }

    // Create new task object with proper formatting
    onAdd({
      ...formData,
      id: Date.now(), // Generate unique ID
      completed: false,
      reward: formData.reward ? parseFloat(formData.reward) : 0 // Convert reward to number
    });

    // Reset form to initial state
    setFormData({
      title: '',
      priority: 'Medium',
      dueDate: '',
      reward: ''
    });
  };

  return (
    <Card className="mb-6">
      <CardHeader>
        <h2 className="text-xl font-semibold">Add New Task</h2>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Task Title Field */}
          <div>
            <label htmlFor="title" className="block text-sm font-medium mb-1">
              Task Title
              <span className="text-red-500 ml-1">*</span>
            </label>
            <Input
              id="title"
              name="title"
              value={formData.title}
              onChange={handleChange}
              placeholder="Enter task title"
              required
            />
          </div>

          {/* Priority Selection */}
          <div>
            <label htmlFor="priority" className="block text-sm font-medium mb-1">
              Priority Level
            </label>
            <select
              id="priority"
              name="priority"
              value={formData.priority}
              onChange={handleChange}
              className="w-full rounded-md border border-gray-300 p-2"
            >
              <option value="Low">Low</option>
              <option value="Medium">Medium</option>
              <option value="High">High</option>
            </select>
          </div>

          {/* Reward Amount Field */}
          <div>
            <label htmlFor="reward" className="block text-sm font-medium mb-1">
              Reward Amount ($)
            </label>
            <Input
              id="reward"
              name="reward"
              type="number"
              min="0"
              step="0.01"
              value={formData.reward}
              onChange={handleChange}
              placeholder="Enter reward amount"
            />
          </div>

          {/* Due Date Field */}
          <div>
            <label htmlFor="dueDate" className="block text-sm font-medium mb-1">
              Due Date
            </label>
            <Input
              id="dueDate"
              name="dueDate"
              type="date"
              value={formData.dueDate}
              onChange={handleChange}
              min={new Date().toISOString().split('T')[0]} // Prevent past dates
            />
          </div>

          <Button type="submit" className="w-full">
            Add Task
          </Button>
        </form>
      </CardContent>
    </Card>
  );
}
