// App.js
import React, { useState, useEffect, useMemo } from "react";
import "./.styles.css"; // Ensure styles are properly linked

// Define specific error types for better error handling
class ValidationError extends Error {
  constructor(fields) {
    super('Validation Failed');
    this.name = 'ValidationError';
    this.fields = fields;
  }
}

class NetworkError extends Error {
  constructor(message = 'Network request failed') {
    super(message);
    this.name = 'NetworkError';
  }
}

// Utility to handle API responses
const handleApiResponse = async (response) => {
  // First check if the response is ok (status in 200-299 range)
  if (!response.ok) {
    const data = await response.json().catch(() => ({}));
    
    // Handle different error status codes
    switch (response.status) {
      case 400:
        // Bad request - usually validation errors
        throw new ValidationError(data.errors || {});
      case 401:
        // Unauthorized - user needs to login
        throw new Error('Please log in to continue.');
      case 403:
        // Forbidden - user doesn't have permission
        throw new Error('You don\'t have permission to perform this action.');
      case 404:
        // Not found
        throw new Error('The requested resource was not found.');
      case 429:
        // Too many requests
        throw new Error('Please wait before trying again.');
      case 500:
        // Server error
        throw new Error('An unexpected error occurred. Please try again later.');
      default:
        // Generic error
        throw new Error(data.message || 'Something went wrong.');
    }
  }

  // If response was ok, return the parsed data
  return response.json();
};

// TaskItem component
function TaskItem({ title, priority, completed, onToggle, onDelete }) {
  const handleClick = (e) => {
    e.stopPropagation(); // Prevents the event from bubbling up to parent elements
    onToggle(); // Calls the toggle function
  };

  return (
    <div className={`task-item ${completed ? 'completed' : ''}`}>
      <input
        type="checkbox"
        checked={completed}
        onChange={handleClick} // Handle checkbox change
      />
      <div>
        <h3>{title}</h3>
        <p>Priority: {priority}</p>
      </div>
      <button onClick={onDelete}>Delete</button> {/* Delete button */}
    </div>
  );
}

// AddTaskForm component
function AddTaskForm({ onAdd }) { 
  const [formData, setFormData] = useState({
    title: '',
    priority: 'Medium',
    dueDate: '',
  });
  const [isSubmitting, setIsSubmitting] = useState(false);  // Track submission state
  const [apiError, setApiError] = useState(null);          // Store API errors
  const [fieldErrors, setFieldErrors] = useState({});      // Store validation errors

  const submitToApi = async (taskData) => {
    try {
      const response = await fetch('/api/tasks', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(taskData)
      });

      return await handleApiResponse(response); // Use the new API response handler
    } catch (error) {
      if (error instanceof TypeError) {
        // Network error (couldn't reach server)
        throw new NetworkError();
      }
      throw error;  // Re-throw other errors
    }
  };

  const validateForm = (data) => {
    const errors = {};
    if (!data.title.trim()) {
      errors.title = 'Task title is required'; // Field-specific error
    }
    return errors;
  };

  const handleChange = (e) => {
    const { name, value } = e.target; // Get name and value from input
    setFormData(prevData => ({
      ...prevData,     // Spread all existing form data
      [name]: value    // Update only the field that changed
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent the default form submission

    // Clear any previous errors
    setApiError(null);
    setFieldErrors({});

    // Validate form data
    const validationErrors = validateForm(formData);
    if (Object.keys(validationErrors).length > 0) {
      setFieldErrors(validationErrors);
      return;
    }

    try {
      // Show loading state
      setIsSubmitting(true);
      
      // Attempt submission
      await submitToApi(formData);
      
      // If successful, clear form and notify parent
      setFormData({
        title: '',
        priority: 'Medium',
        dueDate: '',
      });
      onAdd(formData);
      
    } catch (error) {
      // Handle different types of errors appropriately
      if (error instanceof ValidationError) {
        // Backend validation errors (field-specific)
        setFieldErrors(error.fields);
      } else if (error instanceof NetworkError) {
        // Network-related errors
        setApiError('Unable to connect to the server. Please check your connection.');
      } else {
        // Generic error fallback
        setApiError(error.message || 'An unexpected error occurred.');
      }
    } finally {
      // Always turn off loading state
      setIsSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="add-task-form">
      {/* Show API-level errors at the top of the form */}
      {apiError && (
        <div className="error-message" role="alert">
          {apiError}
        </div>
      )}

      <div className="form-group">
        <label htmlFor="title">
          Task Title:
          <span className="required">*</span>
        </label>
        <input
          id="title"
          name="title"
          type="text"
          value={formData.title}
          onChange={handleChange}
          disabled={isSubmitting} // Disable during submission to prevent double-submit
          className={fieldErrors.title ? 'input-error' : ''}
          aria-invalid={fieldErrors.title ? 'true' : 'false'}
        />
        {/* Show field-specific error messages */}
        {fieldErrors.title && (
          <span className="error-text" role="alert">
            {fieldErrors.title}
          </span>
        )}
      </div>

      <div className="form-group">
        <label htmlFor="priority">Priority Level:</label>
        <select
          id="priority"
          name="priority"
          value={formData.priority}
          onChange={handleChange}
        >
          <option value="Low">Low</option>
          <option value="Medium">Medium</option>
          <option value="High">High</option>
        </select>
      </div>

      <div className="form-group">
        <label htmlFor="dueDate">Due Date:</label>
        <input
          id="dueDate"
          name="dueDate"
          type="date"         // HTML5 date picker
          value={formData.dueDate}
          onChange={handleChange}
        />
      </div>

      <button 
        type="submit" 
        disabled={isSubmitting}
        className={isSubmitting ? 'submitting' : ''}
      >
        {isSubmitting ? 'Adding Task...' : 'Add Task'}
      </button>
    </form>
  );
}

// TaskList component
function TaskList() {
  // State for tasks and filters
  const [tasks, setTasks] = useState(() => {
    const savedTasks = localStorage.getItem('tasks');
    return savedTasks ? JSON.parse(savedTasks) : [];
  });

  // Add sort state
  const [sortConfig, setSortConfig] = useState({
    key: 'createdAt',     // What we're sorting by
    direction: 'desc'     // 'asc' or 'desc'
  });

  // Filter states
  const [filters, setFilters] = useState({
    priority: 'all',        // 'all', 'high', 'medium', 'low'
    status: 'all',          // 'all', 'completed', 'active'
    searchQuery: '',        // Text search
  });

  // Sorting function
  const sortTasks = (tasks) => {
    return [...tasks].sort((a, b) => {
      // Handle different sort keys
      switch (sortConfig.key) {
        case 'title':
          return sortConfig.direction === 'asc'
            ? a.title.localeCompare(b.title)
            : b.title.localeCompare(a.title);
        
        case 'priority': {
          // Convert priority to number for sorting
          const priorityMap = { low: 1, medium: 2, high: 3 };
          const priorityA = priorityMap[a.priority.toLowerCase()];
          const priorityB = priorityMap[b.priority.toLowerCase()];
          return sortConfig.direction === 'asc'
            ? priorityA - priorityB
            : priorityB - priorityA;
        }

        case 'dueDate':
          // Handle potentially missing due dates
          if (!a.dueDate) return sortConfig.direction === 'asc' ? 1 : -1;
          if (!b.dueDate) return sortConfig.direction === 'asc' ? -1 : 1;
          return sortConfig.direction === 'asc'
            ? new Date(a.dueDate) - new Date(b.dueDate)
            : new Date(b.dueDate) - new Date(a.dueDate);

        case 'createdAt':
        default:
          return sortConfig.direction === 'asc'
            ? a.id - b.id
            : b.id - a.id;
      }
    });
  };

  // Memoized filtered tasks to prevent unnecessary recalculations
  const filteredTasks = useMemo(() => {
    return tasks
      // First, filter by priority
      .filter(task => {
        if (filters.priority === 'all') return true;
        return task.priority.toLowerCase() === filters.priority;
      })
      // Then, filter by completion status
      .filter(task => {
        if (filters.status === 'all') return true;
        return filters.status === 'completed' ? task.completed : !task.completed;
      })
      // Finally, filter by search query
      .filter(task => {
        if (!filters.searchQuery) return true;
        return task.title.toLowerCase()
          .includes(filters.searchQuery.toLowerCase());
      });
  }, [tasks, filters]);

  // Update our memoized filtered tasks to include sorting
  const filteredAndSortedTasks = useMemo(() => {
    return sortTasks(filteredTasks);
  }, [filteredTasks, sortConfig]);

  // Handle sort changes
  const handleSort = (key) => {
    setSortConfig(prev => ({
      key,
      // If clicking the same key, toggle direction
      // Otherwise, default to descending
      direction: prev.key === key && prev.direction === 'desc' 
        ? 'asc' 
        : 'desc'
    }));
  };

  // Handler for filter changes
  const handleFilterChange = (filterType, value) => {
    setFilters(prev => ({
      ...prev,
      [filterType]: value
    }));
  };

  const toggleTask = (taskId) => {
    setTasks(prevTasks =>
      prevTasks.map(task =>
        task.id === taskId ? { ...task, completed: !task.completed } : task
      )
    );
  };

  const deleteTask = (taskId) => {
    setTasks(prevTasks => prevTasks.filter(task => task.id !== taskId));
  };

  // Save tasks to local storage when tasks change
  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  return (
    <div className="task-list-container">
      {/* Filter Controls */}
      <div className="filters">
        {/* Priority Filter */}
        <select
          value={filters.priority}
          onChange={(e) => handleFilterChange('priority', e.target.value)}
          aria-label="Filter by priority"
        >
          <option value="all">All Priorities</option>
          <option value="high">High Priority</option>
          <option value="medium">Medium Priority</option>
          <option value="low">Low Priority</option>
        </select>

        {/* Status Filter */}
        <select
          value={filters.status}
          onChange={(e) => handleFilterChange('status', e.target.value)}
          aria-label="Filter by status"
        >
          <option value="all">All Tasks</option>
          <option value="active">Active</option>
          <option value="completed">Completed</option>
        </select>

        {/* Search Input */}
        <input
          type="search"
          placeholder="Search tasks..."
          value={filters.searchQuery}
          onChange={(e) => handleFilterChange('searchQuery', e.target.value)}
          aria-label="Search tasks"
        />
      </div>

      {/* Add Sort Controls */}
      <div className="sort-controls">
        <span>Sort by:</span>
        {['createdAt', 'title', 'priority', 'dueDate'].map(key => (
          <button
            key={key}
            onClick={() => handleSort(key)}
            className={`sort-button ${sortConfig.key === key ? 'active' : ''}`}
          >
            {key.charAt(0).toUpperCase() + key.slice(1)} {/* Capitalize */}
            {sortConfig.key === key && (
              <span className="sort-indicator">
                {sortConfig.direction === 'asc' ? '↑' : '↓'}
              </span>
            )}
          </button>
        ))}
      </div>

      {/* Task Counter */}
      <div className="task-counter" role="status">
        Showing {filteredAndSortedTasks.length} of {tasks.length} tasks
      </div>

      {/* Filtered and Sorted Task List */}
      {filteredAndSortedTasks.length > 0 ? (
        <div className="tasks">
          {filteredAndSortedTasks.map(task => (
            <TaskItem 
              key={task.id}
              title={task.title}
              priority={task.priority}
              completed={task.completed}
              onToggle={() => toggleTask(task.id)}
              onDelete={() => deleteTask(task.id)}
            />
          ))}
        </div>
      ) : (
        <p className="no-results">No tasks match your filters</p>
      )}
    </div>
  );
}

// Main App component
export default function App() {
  const [tasks, setTasks] = useState([]); // Define tasks state

  const addTask = (task) => {
    const newTask = {
      ...task,
      id: Date.now(), // Unique ID for each task
      completed: false,
      createdAt: new Date(), // Timestamp for sorting
    };
    setTasks(prevTasks => [...prevTasks, newTask]);
  };

  return (
    <div className="App">
      <h1>Task Tracker</h1>
      <AddTaskForm onAdd={addTask} />
      <TaskList tasks={tasks} setTasks={setTasks} />
    </div>
  );
}
